import React, {useEffect, useState} from 'react';
import { View, StyleSheet, Share } from 'react-native';
import { Avatar, Title, Caption, Drawer } from 'react-native-paper'
import { DrawerContentScrollView, DrawerItem } from '@react-navigation/drawer';

import Icon from 'react-native-vector-icons/MaterialCommunityIcons';
import AntDesign from 'react-native-vector-icons/AntDesign';
import FontAwesome from 'react-native-vector-icons/FontAwesome';
import Feather from 'react-native-vector-icons/Feather';
import MaterialIcons from 'react-native-vector-icons/MaterialIcons';
import { MaterialCommunityIcons } from '@expo/vector-icons';



import firebase from 'firebase'
require("firebase/firestore")
require("firebase/firebase-storage")

export function DrawerContent(props) {

    const [isDarkTheme, setIsDarkTheme] = React.useState(false);
    const [userData, setUserData] = useState(null);
    
    const getUser = async() => {
        const currentUser = await firestore()
        .collection('users')
        .doc(auth().currentUser.uid)
        .get()
        .then((documentSnapshot) => {
        if( documentSnapshot.exists ) {
            setUserData(documentSnapshot.data());
        }
        })
    }

    useEffect(() => {
        getUser();
    },[]);

    const toggleTheme = () => {
        setIsDarkTheme(!isDarkTheme);
    }

    const onLogout = () => {
        auth().signOut();
    }

    const onShare = async () => {
        try {
          const result = await Share.share({
            message:
              'Check out locctocc! https://apps.apple.com/app/apple-store/id1585460244?pt=123488891&ct=IA&mt=8',
          });
          if (result.action === Share.sharedAction) {
            if (result.activityType) {
              // shared with activity type of result.activityType
            } else {
              // shared
            }
          } else if (result.action === Share.dismissedAction) {
            // dismissed
          }
        } catch (error) {
          alert(error.message);
        }
      };

    return(
        <View style={{ flex: 1 }}>
            <DrawerContentScrollView {...props}>
                <View style={styles.drawerContent}>
                    <View style={styles.userInfoSection}>
                        <View style={{flexDirection: 'row', marginTop: 15}}>
                            <Avatar.Image
                                source={{uri: userData ? userData.userImg : 'https://images.app.goo.gl/7nJRbdq4wXyVLFKV7'}}
                                size={50}
                            />
                            <View style={{marginLeft: 15, flexDirection: 'column'}}>
                                <Title style={styles.title}>{userData ? userData.name : ''}</Title>
                                <Caption style={styles.caption}>{userData ? userData.email : ''}</Caption>
                            </View>
                        </View>
                             
                    </View>
                    
                    <Drawer.Section style={styles.drawerSection}>
                        <DrawerItem 
                            icon={({color, size}) => (
                                <Icon 
                                name="account-outline"
                                color={color}
                                size={size}/>
                            )}
                            label="Edit Profile"
                            onPress={() => {props.navigation.navigate('EditProfile')}}
                        />
                        <DrawerItem 
                            icon={({color, size}) => (
                                <AntDesign
                                name="smileo"
                                color={color}
                                size={size}/>
                            )}
                            label="About Us"
                            onPress={() => {props.navigation.navigate('AboutUs')}}
                        />
                        <DrawerItem 
                            icon={({color, size}) => (
                                <AntDesign 
                                name="notification"
                                color={color}
                                size={size}/>
                            )}
                            label="Community Guidelines"
                            onPress={() => {props.navigation.navigate('HouseGuidelines')}}
                        />
                        <DrawerItem 
                            icon={({color, size}) => (
                                <FontAwesome 
                                name="handshake-o"
                                color={color}
                                size={size}/>
                            )}
                            label="Our Partners"
                            onPress={() => {props.navigation.navigate('Partners')}}
                        />
                    
                        <DrawerItem 
                            icon={({color, size}) => (
                                <MaterialIcons 
                                name="emoji-people"
                                color={color}
                                size={size}/>
                            )}
                            label="Share with friends!"
                            onPress={() => onShare()}
                        />
                        
                        
                    
                    </Drawer.Section>
                </View>
            </DrawerContentScrollView>
            <Drawer.Section style={styles.bottomDrawerSection}>
                <DrawerItem 
                    icon={({color, size}) => (
                        <Icon 
                        name="phone-lock-outline"
                        color={color}
                        size={size}/>
                    )}
                    label="Contact Us"
                    onPress={() => {props.navigation.navigate('ContactUs')}}
                />
                <DrawerItem 
                    icon={({color, size}) => (
                        <Feather
                        name="settings"
                        color={color}
                        size={size}/>
                    )}
                    label="Settings"
                    onPress={() => {props.navigation.navigate('Settings')}}
                />
                <DrawerItem 
                    icon={({color, size}) => (
                        <MaterialCommunityIcons
                        name="shield-lock"
                        color={color}
                        size={size}/>
                    )}
                    label="Privacy"
                    onPress={() => {props.navigation.navigate('LegalDocs')}}
                />
                <DrawerItem 
                    icon={({color, size}) => (
                        <Icon 
                        name="exit-to-app"
                        color={color}
                        size={size}/>
                    )}
                    label="Sign Out"
                    onPress={() => onLogout()}
                />
            </Drawer.Section>

        </View>
    )
}

const styles = StyleSheet.create({
    drawerContent: {
        flex: 1,
    },
    userInfoSection: {
        paddingLeft: 20,
        borderBottomColor: "#f4f4f4",
        borderBottomWidth: 1,

    },
    title: {
        fontSize: 16,
        marginTop: 3,
        fontWeight: 'bold',
    },
    caption: {
        fontSize: 14,
        lineHeight: 14,
        marginBottom: "10%",

    },
    row: {
        marginTop: 20,
        flexDirection: 'row',
        alignItems: 'center',
    },
    section: {
        flexDirection: 'row',
        alignItems: 'center',
        marginRight: 15,
    },
    paragraph: {
        fontWeight: 'bold',
        marginRight: 3,
    },
    drawerSection: {
        marginTop: 15,
    },
    bottomDrawerSection: {
        marginBottom: 15,
        borderTopColor: "#f4f4f4",
        paddingVertical: 12,
        paddingHorizontal: 16,
    },
    preference: {
        flexDirection: 'row',
        justifyContent: 'space-between',
        paddingVertical: 12,
        paddingHorizontal: 16,
    }
})
