    alignItems: 'center',
    justifyContent: 'space-between',
    width: '80%'
  },
  formula1TeamContainer: {
    paddingHorizontal: 10
  },
  listFormula1Text: {
    fontSize: 14
  },
  detailsText: {
    fontSize: 12
  },
  detailsHeader: {
    fontSize: 12,
    textTransform: 'uppercase',
    color: 'grey'
  },
  headerText: {
    fontSize: 16
  },
  headerContainer: {
    alignItems: 'left'
  },
  borderView: {
    borderBottomColor: '#CACFD2',
    borderBottomWidth: 1
  },
  detailsView: {},
  headerView: {
    width: '35%'
  },
  teamContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    paddingBottom: '1%',
    paddingLeft: 4
  }
});

var mapStateToProps = function mapStateToProps(store) {
  return {
    users: store.usersState.users,
    currentUser: store.userState.currentUser,
    following: store.userState.following,
    blocking: store.userState.blocking,
    liked: store.userState.liked,
    faded: store.userState.faded,
    feed: store.usersState.feed,
    usersFollowingLoaded: store.usersState.usersFollowingLoaded,
    futureGames: store.futureGamesState.futureGames,
    formula1Races: store.formula1RacesState.formula1Races
  };
};

var mapDispatchProps = function mapDispatchProps(dispatch) {
  return bindActionCreators({
    fetchUsersData: fetchUsersData
  }, dispatch);
};

export default connect(mapStateToProps, mapDispatchProps)(game);

